package week1.day1;

public class ConvertANegativeNumberToPositiveNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n= -4;
		System.out.println("Negative number"+n);
		if(n<0) {
			int a= n*-1;
			System.out.println("Positive number"+a);
		}

	}

}
